import React, { useState } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';

function SaveUserData() {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [status, setStatus] = useState('');
  const [provider, setProvider] = useState(null);

  const contractAddress = '0x4a8958c6D1fdf3681A4f527Da19B5EFF7Cd79691';

  async function saveDataToBlockchain() {
    let localProvider = null;
    const log = `Name: ${name}, Address: ${userAddress}, Location: ${location}`;
    setStatus('Analyzing data with AI...');

    try {
      // First check if MetaMask is installed and connected
      if (!window.ethereum) {
        alert('Please install MetaMask to use this feature!');
        setStatus('');
        return;
      }

      // Request account access if needed
      try {
        await window.ethereum.request({ method: 'eth_requestAccounts' });
      } catch (error) {
        alert('Please connect your MetaMask wallet!');
        setStatus('');
        return;
      }

      const response = await fetch('http://localhost:5002/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          data: log,
          name: name,
          address: userAddress,
          location: location,
        }),
      });

      const result = await response.json();

      if (!response.ok) {
        let errorMessage = 'Error analyzing data.';
        if (result.attack_type === 'XSS') {
          errorMessage = `Security risk detected in ${result.field}: ${result.reason}\n\nSuggestions:\n${result.suggestions.join('\n')}`;
        } else if (result.attack_type === 'Blockchain Anomaly') {
          errorMessage = `Suspicious transaction pattern detected (Risk Level: ${result.risk_level})`;
        }
        alert(errorMessage);
        setStatus('');
        return;
      }

      console.log('AI Prediction:', result);

      if (result.prediction.toLowerCase() === 'attack') {
        alert('Potential attack detected. Data will not be saved.');
        setStatus('');
        return;
      }

      setStatus('Saving data to the blockchain...');

      // Store provider in state and local variable
      localProvider = new ethers.BrowserProvider(window.ethereum);
      setProvider(localProvider);

      const signer = await localProvider.getSigner();
      const contract = new ethers.Contract(contractAddress, UserDataStorageAbi.abi, signer);

      try {
        console.log('Attempting to save data:', { name, userAddress, location });

        // Get the signer's address
        const from = await signer.getAddress();
        console.log('Signer address:', from);

        // First try to estimate the gas
        let gasEstimate;
        try {
          gasEstimate = await contract.saveUserData.estimateGas(name, userAddress, location);
          console.log('Estimated gas:', gasEstimate.toString());
        } catch (estimateError) {
          console.log('Gas estimation failed, using default:', estimateError);
          gasEstimate = BigInt(500000); // Higher default gas limit
        }

        // Add 20% buffer to gas estimate
        const gasLimit = (gasEstimate * BigInt(120)) / BigInt(100);
        console.log('Gas limit with buffer:', gasLimit.toString());

        // Get current gas price
        const feeData = await localProvider.getFeeData();
        console.log('Current gas price:', feeData.gasPrice.toString());

        // Send transaction with explicit parameters
        const tx = await contract.saveUserData(name, userAddress, location, {
          from: from,
          gasLimit: gasLimit,
          type: 0  // Legacy transaction type
        });

        console.log('Transaction sent:', tx.hash);
        setStatus('Transaction sent, waiting for confirmation...');

        // Wait for the transaction to be mined
        const receipt = await tx.wait(1);
        console.log('Transaction receipt:', receipt);

        if (receipt.status === 1) {
          alert('Data saved successfully to the blockchain!');
          setStatus('Data saved successfully!');

          // Clear form after successful save
          setName('');
          setUserAddress('');
          setLocation('');
        } else {
          throw new Error('Transaction failed');
        }
      } catch (txError) {
        console.error('Transaction error:', txError);

        // Check if the data was actually saved despite the RPC error
        try {
          const events = await contract.queryFilter('UserDataSaved');
          const lastEvent = events[events.length - 1];

          if (lastEvent && lastEvent.args) {
            // Data was saved successfully despite the RPC error
            alert('Data was saved successfully despite the error message.');
            setStatus('Data saved successfully!');

            // Clear form
            setName('');
            setUserAddress('');
            setLocation('');
            return;
          }
        } catch (checkError) {
          console.error('Error checking transaction status:', checkError);
        }

        // If we couldn't confirm the save, show the error
        let errorMessage = 'Transaction failed: ';

        if (txError.code === 'UNPREDICTABLE_GAS_LIMIT') {
          errorMessage += 'Could not estimate gas. Using fixed gas limit.';
        } else if (txError.code === 'INSUFFICIENT_FUNDS') {
          errorMessage += 'Not enough ETH to pay for gas. Please check your wallet balance.';
        } else if (txError.code === 'CALL_EXCEPTION') {
          errorMessage += 'Contract call reverted. Please check your input values.';
        } else if (txError.message?.includes('user rejected')) {
          errorMessage += 'Transaction was rejected by user.';
        } else if (txError.message?.includes('address')) {
          errorMessage += 'Invalid address. Please check MetaMask connection.';
        } else if (txError.data) {
          errorMessage += txError.data.message || txError.message;
        } else {
          errorMessage += txError.message;
        }

        alert(errorMessage);
        setStatus('Transaction failed. Please try again.');
      }

    } catch (error) {
      console.error('Error:', error);
      if (error.code === 4001) {
        // User rejected the transaction
        alert('Transaction was rejected by the user.');
      } else if (error.message?.includes('user rejected')) {
        alert('Transaction was rejected by the user.');
      } else {
        alert('Error saving data: ' + error.message);
      }
      setStatus('');
    } finally {
      if (localProvider) {
        try {
          await localProvider.removeAllListeners();
        } catch (cleanupError) {
          console.log('Cleanup error:', cleanupError);
        }
      }
      setProvider(null);
    }
  }

  async function checkTransaction(txData) {
    try {
      const response = await fetch('/api/analyze-transaction', {
        method: 'POST',
        body: JSON.stringify(txData)
      });

      const analysis = await response.json();

      if (analysis.is_suspicious) {
        displayWarning({
          riskLevel: analysis.risk_level,
          score: analysis.risk_score,
          details: analysis.risk_factors
        });
      }

    } catch (error) {
      console.error('Error analyzing transaction:', error);
    }
  }

  return (
    <div style={styles.formContainer}>
      <h2 style={styles.title}>Save User Data</h2>
      <p>Enter your details to save them to the blockchain.</p>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Name</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your name"
          style={styles.input}
        />
      </div>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Address</label>
        <input
          type="text"
          value={userAddress}
          onChange={(e) => setUserAddress(e.target.value)}
          placeholder="Enter your address"
          style={styles.input}
        />
      </div>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Location</label>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter your location"
          style={styles.input}
        />
      </div>
      <button onClick={saveDataToBlockchain} style={styles.button}>
        Save Data
      </button>
      {status && <p style={styles.status}>{status}</p>}
    </div>
  );
}

const styles = {
  formContainer: {
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    backgroundColor: '#fff',
  },
  title: {
    fontSize: '24px',
    marginBottom: '10px',
    color: '#333',
  },
  inputGroup: {
    marginBottom: '10px',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: 'bold',
  },
  input: {
    width: '100%',
    padding: '8px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '10px 15px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  status: {
    marginTop: '15px',
    color: '#555',
  },
};

export default SaveUserData;
