import React, { useState } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';

function SaveUserData() {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [status, setStatus] = useState('');
  const [provider, setProvider] = useState(null);

  const contractAddress = '0xE77e3c60fF5F11EF4465bcb9326b1fe781c66E7b';

  async function saveDataToBlockchain() {
    let localProvider = null;
    const log = `Name: ${name}, Address: ${userAddress}, Location: ${location}`;
    setStatus('Analyzing data with AI...');

    try {
      // First check if MetaMask is installed and connected
      if (!window.ethereum) {
        alert('Please install MetaMask to use this feature!');
        setStatus('');
        return;
      }

      // Request account access if needed
      try {
        await window.ethereum.request({ method: 'eth_requestAccounts' });
      } catch (error) {
        alert('Please connect your MetaMask wallet!');
        setStatus('');
        return;
      }

      const response = await fetch('http://localhost:5002/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          data: log,
          name: name,
          address: userAddress,
          location: location,
        }),
      });

      const result = await response.json();

      if (!response.ok) {
        let errorMessage = 'Error analyzing data.';
        if (result.attack_type === 'XSS') {
          errorMessage = `Security risk detected in ${result.field}: ${result.reason}\n\nSuggestions:\n${result.suggestions.join('\n')}`;
        } else if (result.attack_type === 'Blockchain Anomaly') {
          errorMessage = `Suspicious transaction pattern detected (Risk Level: ${result.risk_level})`;
        }
        alert(errorMessage);
        setStatus('');
        return;
      }

      console.log('AI Prediction:', result);

      if (result.prediction.toLowerCase() === 'attack') {
        alert('Potential attack detected. Data will not be saved.');
        setStatus('');
        return;
      }

      setStatus('Saving data to the blockchain...');

      // Store provider in state and local variable
      localProvider = new ethers.BrowserProvider(window.ethereum);
      setProvider(localProvider);

      const signer = await localProvider.getSigner();
      const contract = new ethers.Contract(contractAddress, UserDataStorageAbi.abi, signer);

      try {
        console.log('Attempting to save data:', { name, userAddress, location });

        // Get the current nonce
        const nonce = await localProvider.getTransactionCount(await signer.getAddress());
        console.log('Current nonce:', nonce);

        // Get network gas price
        const feeData = await localProvider.getFeeData();
        console.log('Fee data:', {
          gasPrice: feeData.gasPrice.toString(),
          maxFeePerGas: feeData.maxFeePerGas?.toString(),
          maxPriorityFeePerGas: feeData.maxPriorityFeePerGas?.toString()
        });

        // Use a fixed gas limit that we know works
        const gasLimit = 300000;
        console.log('Using fixed gas limit:', gasLimit);

        const tx = await contract.saveUserData(name, userAddress, location, {
          gasLimit: gasLimit,
          nonce: nonce,
          type: 0, // Legacy transaction type
          gasPrice: feeData.gasPrice // Use current gas price
        });

        console.log('Transaction sent:', tx);
        setStatus('Transaction sent, waiting for confirmation...');

        const receipt = await tx.wait();
        console.log('Transaction receipt:', receipt);

        alert('Data saved successfully to the blockchain.');
        setStatus('Data saved successfully!');

        // Clear form after successful save
        setName('');
        setUserAddress('');
        setLocation('');
      } catch (txError) {
        console.error('Transaction error:', txError);

        let errorMessage = 'Transaction failed: ';

        if (txError.code === 'UNPREDICTABLE_GAS_LIMIT') {
          errorMessage += 'Could not estimate gas. The transaction may fail.';
        } else if (txError.code === 'INSUFFICIENT_FUNDS') {
          errorMessage += 'Not enough ETH to pay for gas.';
        } else if (txError.data) {
          errorMessage += txError.data.message || txError.message;
        } else {
          errorMessage += txError.message;
        }

        alert(errorMessage);
        setStatus('Transaction failed. Please try again.');
      }

    } catch (error) {
      console.error('Error:', error);
      if (error.code === 4001) {
        // User rejected the transaction
        alert('Transaction was rejected by the user.');
      } else if (error.message?.includes('user rejected')) {
        alert('Transaction was rejected by the user.');
      } else {
        alert('Error saving data: ' + error.message);
      }
      setStatus('');
    } finally {
      if (localProvider) {
        try {
          await localProvider.removeAllListeners();
        } catch (cleanupError) {
          console.log('Cleanup error:', cleanupError);
        }
      }
      setProvider(null);
    }
  }

  async function checkTransaction(txData) {
    try {
      const response = await fetch('/api/analyze-transaction', {
        method: 'POST',
        body: JSON.stringify(txData)
      });

      const analysis = await response.json();

      if (analysis.is_suspicious) {
        displayWarning({
          riskLevel: analysis.risk_level,
          score: analysis.risk_score,
          details: analysis.risk_factors
        });
      }

    } catch (error) {
      console.error('Error analyzing transaction:', error);
    }
  }

  return (
    <div style={styles.formContainer}>
      <h2 style={styles.title}>Save User Data</h2>
      <p>Enter your details to save them to the blockchain.</p>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Name</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your name"
          style={styles.input}
        />
      </div>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Address</label>
        <input
          type="text"
          value={userAddress}
          onChange={(e) => setUserAddress(e.target.value)}
          placeholder="Enter your address"
          style={styles.input}
        />
      </div>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Location</label>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter your location"
          style={styles.input}
        />
      </div>
      <button onClick={saveDataToBlockchain} style={styles.button}>
        Save Data
      </button>
      {status && <p style={styles.status}>{status}</p>}
    </div>
  );
}

const styles = {
  formContainer: {
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    backgroundColor: '#fff',
  },
  title: {
    fontSize: '24px',
    marginBottom: '10px',
    color: '#333',
  },
  inputGroup: {
    marginBottom: '10px',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: 'bold',
  },
  input: {
    width: '100%',
    padding: '8px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '10px 15px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  status: {
    marginTop: '15px',
    color: '#555',
  },
};

export default SaveUserData;
