import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';

function FetchUserData() {
  const [userAddress, setUserAddress] = useState('');
  const [userData, setUserData] = useState(null);
  const [status, setStatus] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [account, setAccount] = useState('');

  // Update contract address to match current deployment
  const contractAddress = '0x7635615a00cbC897Bd020468C4338B194C8CC948';

  // Check if wallet is connected on component mount
  useEffect(() => {
    checkIfWalletIsConnected();
    checkNetwork();
    // Add listener for account changes
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', () => window.location.reload());
    }
    return () => {
      if (window.ethereum) {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
      }
    };
  }, []);

  const handleAccountsChanged = (accounts) => {
    if (accounts.length > 0) {
      setAccount(accounts[0]);
      setIsConnected(true);
    } else {
      setAccount('');
      setIsConnected(false);
    }
  };

  const checkIfWalletIsConnected = async () => {
    try {
      if (!window.ethereum) {
        setStatus('Please install MetaMask!');
        return;
      }

      const accounts = await window.ethereum.request({ method: 'eth_accounts' });
      if (accounts.length > 0) {
        setAccount(accounts[0]);
        setIsConnected(true);
      }
    } catch (error) {
      console.error('Error checking wallet connection:', error);
    }
  };

  const connectWallet = async () => {
    try {
      if (!window.ethereum) {
        alert('Please install MetaMask!');
        return;
      }

      setStatus('Connecting to MetaMask...');
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setAccount(accounts[0]);
      setIsConnected(true);
      setStatus('Wallet connected successfully!');
    } catch (error) {
      console.error('Error connecting wallet:', error);
      setStatus('Error connecting wallet. Please try again.');
    }
  };

  const checkNetwork = async () => {
    if (window.ethereum) {
      const chainId = await window.ethereum.request({ method: 'eth_chainId' });
      if (chainId !== '0x539') {  // Ganache chainId
        setStatus('Please connect to Ganache network (Chain ID: 1337)');
        try {
          await window.ethereum.request({
            method: 'wallet_switchEthereumChain',
            params: [{ chainId: '0x539' }],
          });
        } catch (error) {
          console.error('Error switching network:', error);
        }
      }
    }
  };

  const fetchDataFromBlockchain = async () => {
    if (!isConnected) {
      setStatus('Please connect your wallet first!');
      return;
    }

    try {
      await checkNetwork();  // Ensure correct network before proceeding
      setStatus('Fetching data...');
      setUserData(null);

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      console.log('Connected with address:', await signer.getAddress());

      // Create contract instance
      const contract = new ethers.Contract(
        contractAddress,
        UserDataStorageAbi,
        signer
      );

      console.log('Fetching data for address:', userAddress);

      let data;
      if (userAddress && userAddress.trim() !== '') {
        // If an address is provided, use getUserDataByAddress
        console.log('Fetching data for specific address:', userAddress);
        data = await contract.getUserDataByAddress(userAddress);
      } else {
        // Otherwise, get all user data
        console.log('Fetching all user data');
        data = await contract.getUserData();
      }

      console.log('Raw data received:', data);

      if (!data || data.length === 0) {
        setStatus('No data found in the contract.');
        return;
      }

      // Parse and display all data entries
      const formattedData = data.map((entry) => ({
        name: entry.name,
        address: entry.userAddress,
        location: entry.location,
      }));

      console.log('Formatted data:', formattedData);

      if (formattedData.length === 0) {
        setStatus('No data found in the contract.');
        return;
      }

      setUserData(formattedData);
      setStatus('Data fetched successfully!');

    } catch (error) {
      console.error('Fetch error:', error);
      if (error.code === 'CALL_EXCEPTION') {
        setStatus('Error: Contract call failed. Please verify the contract address and network connection.');
      } else if (error.code === 'INVALID_ARGUMENT') {
        setStatus('Error: Invalid Ethereum address format. Please check the address and try again.');
      } else {
        setStatus('Error fetching data: ' + (error.message || 'Unknown error'));
      }
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Fetch User Data</h2>

      {!isConnected ? (
        <div style={styles.connectContainer}>
          <p>Please connect your wallet to fetch data.</p>
          <button onClick={connectWallet} style={styles.connectButton}>
            Connect Wallet
          </button>
        </div>
      ) : (
        <>
          <p style={styles.connectedStatus}>
            Connected: {account.slice(0, 6)}...{account.slice(-4)}
          </p>
          <p>Enter an Ethereum address to fetch the stored data from the blockchain.</p>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Ethereum Address</label>
            <input
              type="text"
              value={userAddress}
              onChange={(e) => setUserAddress(e.target.value)}
              placeholder="Enter Ethereum address"
              style={styles.input}
            />
          </div>
          <button onClick={fetchDataFromBlockchain} style={styles.button}>
            Fetch Data
          </button>
        </>
      )}

      {status && <p style={styles.status}>{status}</p>}

      {userData && userData.length > 0 && (
        <div style={styles.resultContainer}>
          <h3 style={styles.resultTitle}>Fetched Data:</h3>
          {userData.map((entry, index) => (
            <div key={index} style={styles.entry}>
              <p><strong>Name:</strong> {entry.name}</p>
              <p><strong>Address:</strong> {entry.address}</p>
              <p><strong>Location:</strong> {entry.location}</p>
              <hr style={styles.divider} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    maxWidth: '600px',
    margin: 'auto',
  },
  title: {
    fontSize: '24px',
    marginBottom: '15px',
    color: '#333',
  },
  inputGroup: {
    marginBottom: '15px',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: 'bold',
    color: '#555',
  },
  input: {
    width: '100%',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
  },
  button: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '10px 15px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
  status: {
    marginTop: '15px',
    color: '#555',
  },
  resultContainer: {
    marginTop: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
    padding: '15px',
    border: '1px solid #ddd',
  },
  resultTitle: {
    marginBottom: '10px',
    color: '#333',
    fontSize: '20px',
    fontWeight: 'bold',
  },
  connectContainer: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  connectButton: {
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    padding: '12px 24px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    marginTop: '10px',
  },
  connectedStatus: {
    color: '#28a745',
    fontWeight: 'bold',
    marginBottom: '15px',
  },
};

export default FetchUserData;
