import React, { useState } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json'; // Ensure this ABI file exists

function FetchUserData() {
  const [userAddress, setUserAddress] = useState('');
  const [userData, setUserData] = useState(null);
  const [status, setStatus] = useState('');

  // Replace with your deployed contract address
  const contractAddress = '0x66425E9e3701634Be565253CF84e27480806d2eD';

  const fetchDataFromBlockchain = async () => {
    if (!window.ethereum) {
      alert('Please install MetaMask!');
      return;
    }

    try {
      // Request account access
      await window.ethereum.request({ method: 'eth_requestAccounts' });

      // Create a provider
      const provider = new ethers.BrowserProvider(window.ethereum); // Updated for ethers v6
      const signer = await provider.getSigner(); // `getSigner()` is now asynchronous

      // Create a contract instance
      const contract = new ethers.Contract(contractAddress, UserDataStorageAbi.abi, signer);

      // Call getUserData function in the contract
      const data = await contract.getUserData(userAddress);
      setUserData({
        name: data[0],
        address: data[1],
        location: data[2],
      });
      setStatus('Data fetched successfully!');
    } catch (error) {
      console.error(error);
      setStatus('Error fetching data.');
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Fetch User Data</h2>
      <p>Enter an Ethereum address to fetch the stored data from the blockchain.</p>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Ethereum Address</label>
        <input
          type="text"
          value={userAddress}
          onChange={(e) => setUserAddress(e.target.value)}
          placeholder="Enter Ethereum address"
          style={styles.input}
        />
      </div>
      <button onClick={fetchDataFromBlockchain} style={styles.button}>
        Fetch Data
      </button>
      {status && <p style={styles.status}>{status}</p>}
      {userData && (
        <div style={styles.resultContainer}>
          <h3 style={styles.resultTitle}>Fetched Data:</h3>
          <p>
            <strong>Name:</strong> {userData.name}
          </p>
          <p>
            <strong>Address:</strong> {userData.address}
          </p>
          <p>
            <strong>Location:</strong> {userData.location}
          </p>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    maxWidth: '600px',
    margin: 'auto',
  },
  title: {
    fontSize: '24px',
    marginBottom: '15px',
    color: '#333',
  },
  inputGroup: {
    marginBottom: '15px',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: 'bold',
    color: '#555',
  },
  input: {
    width: '100%',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
  },
  button: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '10px 15px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
  status: {
    marginTop: '15px',
    color: '#555',
  },
  resultContainer: {
    marginTop: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '8px',
    padding: '15px',
    border: '1px solid #ddd',
  },
  resultTitle: {
    marginBottom: '10px',
    color: '#333',
    fontSize: '20px',
    fontWeight: 'bold',
  },
};

export default FetchUserData;
