import React, { useState, useEffect } from 'react';
import { BrowserProvider, Contract } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';
import './SaveUserData.css';

const SaveUserData = () => {
  const [isConnected, setIsConnected] = useState(false);
  const [account, setAccount] = useState('');
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [status, setStatus] = useState({ type: '', message: '' });
  const [isLoading, setIsLoading] = useState(false);
  const contractAddress = '0x7635615a00cbC897Bd020468C4338B194C8CC948';

  useEffect(() => {
    checkIfWalletIsConnected();
    window.ethereum.on('accountsChanged', handleAccountsChanged);
    return () => {
      window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
    };
  }, []);

  const checkIfWalletIsConnected = async () => {
    try {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        if (accounts.length > 0) {
          setAccount(accounts[0]);
          setIsConnected(true);
          setUserAddress(accounts[0]); // Auto-fill the address field
        }
      }
    } catch (error) {
      console.error('Error checking wallet connection:', error);
    }
  };

  const handleAccountsChanged = (accounts) => {
    if (accounts.length > 0) {
      setAccount(accounts[0]);
      setIsConnected(true);
      setUserAddress(accounts[0]); // Update address field when account changes
    } else {
      setIsConnected(false);
      setAccount('');
    }
  };

  const connectWallet = async () => {
    try {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]);
        setIsConnected(true);
        setUserAddress(accounts[0]); // Auto-fill the address field
        setStatus({ type: 'success', message: 'Wallet connected successfully! ‚úÖ' });
      } else {
        setStatus({ type: 'error', message: 'Please install MetaMask! ‚ùå' });
      }
    } catch (error) {
      setStatus({ type: 'error', message: 'Error connecting wallet: ' + error.message });
    }
  };

  const copyAddress = () => {
    navigator.clipboard.writeText(account);
    setStatus({ type: 'info', message: 'Address copied to clipboard! üìã' });
    setTimeout(() => setStatus({ type: '', message: '' }), 3000);
  };

  const saveDataToBlockchain = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setStatus({ type: '', message: '' });

    try {
      if (!window.ethereum) throw new Error('Please install MetaMask');

      const provider = new BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new Contract(contractAddress, UserDataStorageAbi, signer);

      console.log('Saving data:', { name, userAddress, location });

      const tx = await contract.saveUserData(name, userAddress, location, {
        gasLimit: 300000
      });

      setStatus({ type: 'info', message: 'Transaction submitted, waiting for confirmation... ‚è≥' });

      const receipt = await tx.wait(1);
      console.log('Transaction receipt:', receipt);

      setStatus({ type: 'success', message: 'Data saved successfully! ‚úÖ' });
      setName('');
      setLocation('');

    } catch (error) {
      console.error('Error:', error);
      setStatus({ type: 'error', message: 'Error saving data: ' + error.message + ' ‚ùå' });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="save-data-container">
      <div className="save-data-card">
        <div className="card-header">
          <i className="fas fa-save"></i>
          <h2>Save User Data</h2>
        </div>

        {!isConnected ? (
          <div className="connect-wallet-section">
            <button onClick={connectWallet} className="connect-wallet-btn">
              <i className="fas fa-wallet"></i>
              Connect Wallet
            </button>
          </div>
        ) : (
          <div className="wallet-info">
            <span className="connected-text">
              <i className="fas fa-circle connected-icon"></i>
              Connected:
            </span>
            <span className="account-address" onClick={copyAddress} title="Click to copy">
              {account.substring(0, 6)}...{account.substring(38)}
              <i className="fas fa-copy copy-icon"></i>
            </span>
          </div>
        )}

        {status.message && (
          <div className={`status-message ${status.type}`}>
            {status.message}
          </div>
        )}

        <form onSubmit={saveDataToBlockchain} className="save-data-form">
          <div className="form-group">
            <label>
              <i className="fas fa-user"></i>
              Name
            </label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label>
              <i className="fas fa-address-card"></i>
              Ethereum Address
            </label>
            <input
              type="text"
              value={userAddress}
              onChange={(e) => setUserAddress(e.target.value)}
              placeholder="Enter Ethereum address"
              required
            />
          </div>

          <div className="form-group">
            <label>
              <i className="fas fa-map-marker-alt"></i>
              Location
            </label>
            <input
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="Enter your location"
              required
            />
          </div>

          <button 
            type="submit" 
            className={`submit-btn ${isLoading ? 'loading' : ''}`}
            disabled={!isConnected || isLoading}
          >
            {isLoading ? (
              <>
                <i className="fas fa-spinner fa-spin"></i>
                Saving...
              </>
            ) : (
              <>
                <i className="fas fa-save"></i>
                Save Data
              </>
            )}
          </button>
        </form>
      </div>
    </div>
  );
};

export default SaveUserData;
