import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';
import './FetchUserData.css';

const FetchUserData = () => {
  const [isConnected, setIsConnected] = useState(false);
  const [account, setAccount] = useState('');
  const [searchAddress, setSearchAddress] = useState('');
  const [userData, setUserData] = useState(null);
  const [status, setStatus] = useState({ type: '', message: '' });
  const [isLoading, setIsLoading] = useState(false);

  const contractAddress = '0x7635615a00cbC897Bd020468C4338B194C8CC948';

  useEffect(() => {
    checkIfWalletIsConnected();
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', () => window.location.reload());
    }
    return () => {
      if (window.ethereum) {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
      }
    };
  }, []);

  const checkIfWalletIsConnected = async () => {
    try {
      if (!window.ethereum) {
        setStatus({ type: 'error', message: 'Please install MetaMask!' });
        return;
      }
      const accounts = await window.ethereum.request({ method: 'eth_accounts' });
      if (accounts.length > 0) {
        setAccount(accounts[0]);
        setIsConnected(true);
      }
    } catch (error) {
      console.error('Error checking wallet connection:', error);
      setStatus({ type: 'error', message: 'Error connecting to wallet' });
    }
  };

  const handleAccountsChanged = (accounts) => {
    if (accounts.length > 0) {
      setAccount(accounts[0]);
      setIsConnected(true);
    } else {
      setAccount('');
      setIsConnected(false);
    }
  };

  const connectWallet = async () => {
    try {
      if (!window.ethereum) {
        setStatus({ type: 'error', message: 'Please install MetaMask!' });
        return;
      }
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setAccount(accounts[0]);
      setIsConnected(true);
      setStatus({ type: 'success', message: 'Wallet connected successfully!' });
    } catch (error) {
      console.error('Error connecting wallet:', error);
      setStatus({ type: 'error', message: 'Failed to connect wallet' });
    }
  };

  const fetchDataFromBlockchain = async () => {
    setIsLoading(true);
    setStatus({ type: '', message: '' });
    try {
      if (!window.ethereum) {
        throw new Error('Please install MetaMask!');
      }

      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const contract = new ethers.Contract(contractAddress, UserDataStorageAbi, provider);

      let data;
      if (searchAddress.trim()) {
        data = await contract.getUserDataByAddress(searchAddress);
      } else {
        data = await contract.getUserData();
      }

      if (!data || (Array.isArray(data) && data.length === 0)) {
        setUserData(null);
        setStatus({ type: 'info', message: 'No data found' });
        return;
      }

      const formattedData = Array.isArray(data) ? data : [data];
      const processedData = formattedData.map(item => ({
        name: item.name || 'N/A',
        userAddress: item.userAddress || 'N/A',
        location: item.location || 'N/A',
        timestamp: new Date(item.timestamp * 1000).toLocaleString()
      }));

      setUserData(processedData);
      setStatus({ type: 'success', message: 'Data fetched successfully!' });
    } catch (error) {
      console.error('Error fetching data:', error);
      setStatus({ type: 'error', message: 'Error fetching data: ' + error.message });
      setUserData(null);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="fetch-data-container">
      <div className="fetch-data-card">
        <div className="card-header">
          <h1>Fetch User Data</h1>
          {!isConnected ? (
            <button className="connect-wallet-btn" onClick={connectWallet}>
              <i className="fas fa-wallet"></i>
              Connect Wallet
            </button>
          ) : (
            <div className="wallet-info">
              <i className="fas fa-check-circle"></i>
              {`${account.substring(0, 6)}...${account.substring(38)}`}
            </div>
          )}
        </div>

        <div className="search-section">
          <div className="form-group">
            <label>
              <i className="fas fa-search"></i>
              Search by Address (Optional)
            </label>
            <input
              type="text"
              value={searchAddress}
              onChange={(e) => setSearchAddress(e.target.value)}
              placeholder="Enter Ethereum address"
            />
          </div>
          <button
            className="fetch-btn"
            onClick={fetchDataFromBlockchain}
            disabled={!isConnected || isLoading}
          >
            {isLoading ? (
              <div className="loader"></div>
            ) : (
              <>
                <i className="fas fa-database"></i>
                Fetch Data
              </>
            )}
          </button>
        </div>

        {status.message && (
          <div className={`status-message ${status.type}`}>
            {status.type === 'success' && <i className="fas fa-check-circle"></i>}
            {status.type === 'error' && <i className="fas fa-exclamation-circle"></i>}
            {status.type === 'info' && <i className="fas fa-info-circle"></i>}
            {status.message}
          </div>
        )}

        {userData && userData.length > 0 && (
          <div className="data-table-container">
            <table className="data-table">
              <thead>
                <tr>
                  <th><i className="fas fa-user"></i> Name</th>
                  <th><i className="fas fa-address-card"></i> Address</th>
                  <th><i className="fas fa-map-marker-alt"></i> Location</th>
                  <th><i className="fas fa-clock"></i> Timestamp</th>
                </tr>
              </thead>
              <tbody>
                {userData.map((data, index) => (
                  <tr key={index}>
                    <td>{data.name}</td>
                    <td className="address-cell" title={data.userAddress}>
                      {`${data.userAddress.substring(0, 6)}...${data.userAddress.substring(38)}`}
                    </td>
                    <td>{data.location}</td>
                    <td>{data.timestamp}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default FetchUserData;
