import React, { useState } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';

function SaveUserData() {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [status, setStatus] = useState('');

  const contractAddress = '0x102297dDc08BB7a31eDdd5a8223C5b7F441e3020';

  async function saveDataToBlockchain(name, address, location) {
    // Combine data into a single string for AI model analysis
    const log = `Name: ${name}, Address: ${address}, Location: ${location}`;

    // Step 1: Send the data to the AI model API
    try {
        const response = await fetch('http://localhost:5002/predict', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ data: log }),
        });

        const result = await response.json();

        if (result.error) {
            alert('Error analyzing data: ' + result.error);
            return;
        }

        console.log('AI Prediction:', result);

        // Step 2: Check the prediction
        if (result.prediction.toLowerCase() === 'attack') {
            alert('Potential attack detected. Data will not be saved.');
            return; // Stop the process if an attack is detected
        }

        // Step 3: Save data to the blockchain if no attack is detected
        try {
            // Get the signer from MetaMask
            const provider = new ethers.BrowserProvider(window.ethereum);
            const signer = await provider.getSigner();

            // Create a contract instance
            const contract = new ethers.Contract(contractAddress, UserDataStorageAbi.abi, signer);

            // Call the smart contract function
            const tx = await contract.saveUserData(name, address, location);

            await tx.wait(); // Wait for the transaction to be mined
            alert('Data saved successfully to the blockchain.');
        } catch (blockchainError) {
            console.error('Error interacting with the blockchain:', blockchainError);
            alert('Error saving data to the blockchain.');
        }
    } catch (error) {
        console.error('Error saving data:', error);
        alert('Error saving data. See console for details.');
    }
}

  return (
    <div style={styles.formContainer}>
      <h2 style={styles.title}>Save User Data</h2>
      <p>Enter your details to save them to the blockchain.</p>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Name</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your name"
          style={styles.input}
        />
      </div>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Address</label>
        <input
          type="text"
          value={userAddress}
          onChange={(e) => setUserAddress(e.target.value)}
          placeholder="Enter your address"
          style={styles.input}
        />
      </div>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Location</label>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter your location"
          style={styles.input}
        />
      </div>
      <button onClick={saveDataToBlockchain} style={styles.button}>
        Save Data
      </button>
      {status && <p style={styles.status}>{status}</p>}
    </div>
  );
}

const styles = {
  formContainer: {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
  },
  title: {
    fontSize: '24px',
    marginBottom: '15px',
    color: '#333',
  },
  inputGroup: {
    marginBottom: '15px',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: 'bold',
    color: '#555',
  },
  input: {
    width: '100%',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
  },
  button: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '10px 15px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
  status: {
    marginTop: '15px',
    color: '#555',
  },
};

export default SaveUserData;
