import React, { useState } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';

function SaveUserData() {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [status, setStatus] = useState('');

  const contractAddress = '0x102297dDc08BB7a31eDdd5a8223C5b7F441e3020';

  const saveDataToBlockchain = async () => {
    if (!window.ethereum) {
      alert('Please install MetaMask!');
      return;
    }

    try {
      await window.ethereum.request({ method: 'eth_requestAccounts' });
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, UserDataStorageAbi.abi, signer);
      const tx = await contract.saveUserData(name, userAddress, location);
      setStatus('Saving data...');
      await tx.wait();
      setStatus('Data saved successfully!');
    } catch (error) {
      console.error(error);
      setStatus('Error saving data.');
    }
  };

  return (
    <div style={styles.formContainer}>
      <h2 style={styles.title}>Save User Data</h2>
      <p>Enter your details to save them to the blockchain.</p>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Name</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter your name"
          style={styles.input}
        />
      </div>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Address</label>
        <input
          type="text"
          value={userAddress}
          onChange={(e) => setUserAddress(e.target.value)}
          placeholder="Enter your address"
          style={styles.input}
        />
      </div>
      <div style={styles.inputGroup}>
        <label style={styles.label}>Location</label>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter your location"
          style={styles.input}
        />
      </div>
      <button onClick={saveDataToBlockchain} style={styles.button}>
        Save Data
      </button>
      {status && <p style={styles.status}>{status}</p>}
    </div>
  );
}

const styles = {
  formContainer: {
    backgroundColor: 'white',
    borderRadius: '8px',
    padding: '20px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
  },
  title: {
    fontSize: '24px',
    marginBottom: '15px',
    color: '#333',
  },
  inputGroup: {
    marginBottom: '15px',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: 'bold',
    color: '#555',
  },
  input: {
    width: '100%',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
  },
  button: {
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    padding: '10px 15px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
  status: {
    marginTop: '15px',
    color: '#555',
  },
};

export default SaveUserData;
