import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';
import './SaveUserData.css';

const SaveUserData = () => {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const [statusType, setStatusType] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [account, setAccount] = useState('');

  const contractAddress = '0x7635615a00cbC897Bd020468C4338B194C8CC948';

  useEffect(() => {
    checkIfWalletIsConnected();
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', () => window.location.reload());
    }
    return () => {
      if (window.ethereum) {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
      }
    };
  }, []);

  const checkIfWalletIsConnected = async () => {
    try {
      const accounts = await window.ethereum.request({ method: 'eth_accounts' });
      if (accounts.length > 0) {
        setAccount(accounts[0]);
        setIsConnected(true);
      }
    } catch (error) {
      console.error('Error checking wallet connection:', error);
    }
  };

  const handleAccountsChanged = (accounts) => {
    if (accounts.length > 0) {
      setAccount(accounts[0]);
      setIsConnected(true);
    } else {
      setIsConnected(false);
      setAccount('');
    }
  };

  const connectWallet = async () => {
    try {
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setAccount(accounts[0]);
      setIsConnected(true);
      setStatus('Wallet connected successfully!');
      setStatusType('success');
    } catch (error) {
      setStatus('Failed to connect wallet. Please try again.');
      setStatusType('error');
    }
  };

  const saveDataToBlockchain = async (e) => {
    e.preventDefault();
    if (!isConnected) {
      setStatus('Please connect your wallet first');
      setStatusType('error');
      return;
    }

    setLoading(true);
    setStatus('Analyzing data with AI...');
    setStatusType('info');

    try {
      // First, analyze with AI
      const aiResponse = await fetch('http://localhost:5002/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, userAddress, location })
      });

      if (!aiResponse.ok) throw new Error('AI analysis failed');
      const aiResult = await aiResponse.json();

      if (aiResult.prediction === 'malicious') {
        setStatus('‚ö†Ô∏è Potentially malicious input detected. Transaction cancelled.');
        setStatusType('error');
        setLoading(false);
        return;
      }

      // If AI check passes, proceed with blockchain transaction
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(contractAddress, UserDataStorageAbi, signer);

      setStatus('üìù Preparing to save data to blockchain...');
      const tx = await contract.saveUserData(name, userAddress, location, {
        gasLimit: 300000
      });

      setStatus('‚è≥ Transaction pending...');
      const receipt = await tx.wait(1);

      if (receipt.status === 1) {
        setStatus('‚úÖ Data saved successfully!');
        setStatusType('success');
        // Clear form
        setName('');
        setUserAddress('');
        setLocation('');
      } else {
        setStatus('‚ùå Transaction failed. Please try again.');
        setStatusType('error');
      }
    } catch (error) {
      console.error('Error:', error);
      setStatus(`‚ùå ${error.message || 'Transaction failed. Please try again.'}`);
      setStatusType('error');
    }

    setLoading(false);
  };

  return (
    <div className="save-data-container">
      <div className="save-data-card">
        <h1>Save User Data</h1>

        {!isConnected ? (
          <div className="connect-wallet-section">
            <p>Please connect your wallet to save data</p>
            <button onClick={connectWallet} className="connect-button">
              <i className="fas fa-wallet"></i> Connect Wallet
            </button>
          </div>
        ) : (
          <div className="wallet-info">
            <span className="wallet-badge">
              <i className="fas fa-check-circle"></i>
              Connected: {account.slice(0, 6)}...{account.slice(-4)}
            </span>
          </div>
        )}

        <form onSubmit={saveDataToBlockchain} className="save-data-form">
          <div className="form-group">
            <label htmlFor="name">
              <i className="fas fa-user"></i> Name
            </label>
            <input
              id="name"
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter your name"
              required
              disabled={!isConnected || loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="userAddress">
              <i className="fas fa-map-marker-alt"></i> User Address
            </label>
            <input
              id="userAddress"
              type="text"
              value={userAddress}
              onChange={(e) => setUserAddress(e.target.value)}
              placeholder="Enter your address"
              required
              disabled={!isConnected || loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="location">
              <i className="fas fa-globe"></i> Location
            </label>
            <input
              id="location"
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="Enter your location"
              required
              disabled={!isConnected || loading}
            />
          </div>

          {status && (
            <div className={`status-message ${statusType}`}>
              {status}
            </div>
          )}

          <button 
            type="submit" 
            className={`submit-button ${loading ? 'loading' : ''}`}
            disabled={!isConnected || loading}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                Processing...
              </>
            ) : (
              <>
                <i className="fas fa-save"></i>
                Save to Blockchain
              </>
            )}
          </button>
        </form>
      </div>
    </div>
  );
};

export default SaveUserData;
