import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';

function SaveUserData() {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [status, setStatus] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [account, setAccount] = useState('');

  const contractAddress = '0x7635615a00cbC897Bd020468C4338B194C8CC948';

  // Check if wallet is connected on component mount
  useEffect(() => {
    checkIfWalletIsConnected();
    // Add listener for account changes
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', () => window.location.reload());
    }
    return () => {
      if (window.ethereum) {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
      }
    };
  }, []);

  const handleAccountsChanged = (accounts) => {
    if (accounts.length > 0) {
      setAccount(accounts[0]);
      setIsConnected(true);
    } else {
      setAccount('');
      setIsConnected(false);
    }
  };

  const checkIfWalletIsConnected = async () => {
    try {
      if (!window.ethereum) {
        setStatus('Please install MetaMask!');
        return;
      }

      const accounts = await window.ethereum.request({ method: 'eth_accounts' });
      if (accounts.length > 0) {
        setAccount(accounts[0]);
        setIsConnected(true);
      }
    } catch (error) {
      console.error('Error checking wallet connection:', error);
    }
  };

  const connectWallet = async () => {
    try {
      if (!window.ethereum) {
        alert('Please install MetaMask!');
        return;
      }

      setStatus('Connecting to MetaMask...');
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setAccount(accounts[0]);
      setIsConnected(true);
      setStatus('Wallet connected successfully!');
    } catch (error) {
      console.error('Error connecting wallet:', error);
      setStatus('Error connecting wallet. Please try again.');
    }
  };

  const saveDataToBlockchain = async () => {
    if (!isConnected) {
      setStatus('Please connect your wallet first!');
      return;
    }

    try {
      setStatus('Saving data to blockchain...');

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const signerAddress = await signer.getAddress();

      console.log('Connected with address:', signerAddress);

      const contract = new ethers.Contract(
        contractAddress,
        UserDataStorageAbi,
        signer
      );

      console.log('Saving data:', { name, userAddress, location });

      // Create transaction with simplified parameters
      const tx = await contract.saveUserData(
        name,
        userAddress,
        location,
        {
          from: signerAddress,
          gasLimit: ethers.parseUnits("300000", "wei") // Fixed gas limit
        }
      );

      console.log('Transaction hash:', tx.hash);
      setStatus('Transaction sent! Waiting for confirmation...');

      // Wait for transaction confirmation
      const receipt = await tx.wait();
      console.log('Transaction confirmed:', receipt);

      if (receipt.status === 1) {
        setStatus('Data saved successfully!');
        // Clear form after successful save
        setName('');
        setUserAddress('');
        setLocation('');
      } else {
        setStatus('Transaction failed. Please try again.');
      }

    } catch (error) {
      console.error('Save error:', error);
      if (error.code === 'ACTION_REJECTED') {
        setStatus('Transaction was rejected by user.');
      } else if (error.code === 'INSUFFICIENT_FUNDS') {
        setStatus('Error: Insufficient funds for gas. Please check your balance.');
      } else {
        setStatus('Error saving data: ' + (error.message || 'Unknown error'));
      }
    }
  };

  async function checkTransaction(txData) {
    try {
      const response = await fetch('/api/analyze-transaction', {
        method: 'POST',
        body: JSON.stringify(txData)
      });

      const analysis = await response.json();

      if (analysis.is_suspicious) {
        displayWarning({
          riskLevel: analysis.risk_level,
          score: analysis.risk_score,
          details: analysis.risk_factors
        });
      }

    } catch (error) {
      console.error('Error analyzing transaction:', error);
    }
  }

  return (
    <div style={styles.formContainer}>
      <h2 style={styles.title}>Save User Data</h2>

      {!isConnected ? (
        <div style={styles.connectContainer}>
          <p>Please connect your wallet to save data.</p>
          <button onClick={connectWallet} style={styles.connectButton}>
            Connect Wallet
          </button>
        </div>
      ) : (
        <>
          <p style={styles.connectedStatus}>
            Connected: {account.slice(0, 6)}...{account.slice(-4)}
          </p>
          <p>Enter your details to save them to the blockchain.</p>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter your name"
              style={styles.input}
            />
          </div>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Address</label>
            <input
              type="text"
              value={userAddress}
              onChange={(e) => setUserAddress(e.target.value)}
              placeholder="Enter your address"
              style={styles.input}
            />
          </div>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Location</label>
            <input
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="Enter your location"
              style={styles.input}
            />
          </div>
          <button onClick={saveDataToBlockchain} style={styles.button}>
            Save Data
          </button>
        </>
      )}
      {status && <p style={styles.status}>{status}</p>}
    </div>
  );
}

const styles = {
  formContainer: {
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    backgroundColor: '#fff',
  },
  title: {
    fontSize: '24px',
    marginBottom: '10px',
    color: '#333',
  },
  inputGroup: {
    marginBottom: '10px',
  },
  label: {
    display: 'block',
    marginBottom: '5px',
    fontWeight: 'bold',
  },
  input: {
    width: '100%',
    padding: '8px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '10px 15px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  status: {
    marginTop: '15px',
    color: '#555',
  },
  connectContainer: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  connectButton: {
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    padding: '12px 24px',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    marginTop: '10px',
  },
  connectedStatus: {
    color: '#28a745',
    fontWeight: 'bold',
    marginBottom: '15px',
  },
};

export default SaveUserData;
