import React, { useState, useEffect } from 'react';
import { BrowserProvider, Contract } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';
import './SaveUserData.css';

const SaveUserData = () => {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [account, setAccount] = useState('');
  const [aiAnalysisComplete, setAiAnalysisComplete] = useState(false);
  const contractAddress = '0x7635615a00cbC897Bd020468C4338B194C8CC948';

  useEffect(() => {
    checkIfWalletIsConnected();
    window.ethereum.on('accountsChanged', handleAccountsChanged);
    window.ethereum.on('chainChanged', () => window.location.reload());

    return () => {
      window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
    };
  }, []);

  const checkIfWalletIsConnected = async () => {
    try {
      const accounts = await window.ethereum.request({ method: 'eth_accounts' });
      if (accounts.length > 0) {
        setAccount(accounts[0]);
        setIsConnected(true);
      }
    } catch (error) {
      console.error('Error checking wallet connection:', error);
    }
  };

  const handleAccountsChanged = (accounts) => {
    if (accounts.length > 0) {
      setAccount(accounts[0]);
      setIsConnected(true);
    } else {
      setIsConnected(false);
      setAccount('');
    }
  };

  const connectWallet = async () => {
    try {
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setAccount(accounts[0]);
      setIsConnected(true);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const saveDataToBlockchain = async (e) => {
    e.preventDefault();

    // Validate Ethereum address
    if (!userAddress.match(/^0x[0-9a-fA-F]{40}$/)) {
      setStatus({
        type: 'error',
        message: 'Error: Please enter a valid Ethereum address (42 characters starting with 0x) ❌'
      });
      return;
    }

    setLoading(true);
    setAiAnalysisComplete(false);
    setStatus({ type: 'info', message: 'Analyzing data with AI...' });

    try {
      // First, analyze data with AI
      console.log('Sending data to AI endpoint:', { name, userAddress, location });
      const aiResponse = await fetch('http://localhost:5002/predict', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        mode: 'cors',
        credentials: 'include',
        body: JSON.stringify({ name, userAddress, location })
      });

      if (!aiResponse.ok) {
        let errorMessage;
        try {
          const errorData = await aiResponse.json();
          errorMessage = errorData.error || `Server error: ${aiResponse.status}`;
        } catch (e) {
          errorMessage = `Network error: ${aiResponse.status} ${aiResponse.statusText}`;
          if (aiResponse.status === 0) {
            errorMessage = 'Cannot connect to AI server. Please ensure the backend is running on port 5002.';
          }
        }
        console.error('AI analysis failed:', errorMessage);
        throw new Error(errorMessage);
      }

      const aiResult = await aiResponse.json();
      console.log('AI analysis result:', aiResult);
      setAiAnalysisComplete(true);
      setStatus({ type: 'success', message: 'AI analysis complete ✅ Saving to blockchain...' });

      // Then save to blockchain
      const provider = new BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new Contract(contractAddress, UserDataStorageAbi, signer);

      // Get gas estimate with a 20% buffer
      const gasEstimate = await contract.saveUserData.estimateGas(name, userAddress, location);
      console.log('Estimated gas:', gasEstimate.toString());

      const gasLimit = (gasEstimate * BigInt(120)) / BigInt(100); // Add 20% buffer
      console.log('Gas limit:', gasLimit.toString());

      const tx = await contract.saveUserData(name, userAddress, location, {
        gasLimit: gasLimit
      });

      console.log('Transaction hash:', tx.hash);
      setStatus({ type: 'info', message: 'Transaction sent! Waiting for confirmation...' });

      const receipt = await tx.wait(1);
      console.log('Transaction receipt:', receipt);

      if (receipt.status === 1) {
        setStatus({ type: 'success', message: 'Data saved successfully! ✅' });
        setName('');
        setUserAddress('');
        setLocation('');
        setAiAnalysisComplete(false);
      } else {
        setStatus({ type: 'error', message: 'Transaction failed. Please try again. ❌' });
      }
    } catch (error) {
      console.error('Detailed error:', error);
      let errorMessage = 'An error occurred. Please try again.';

      if (error.message.includes('AI analysis')) {
        errorMessage = `AI analysis failed: ${error.message}`;
      } else if (error.code === 'UNPREDICTABLE_GAS_LIMIT') {
        errorMessage = 'Error estimating gas. Please check if the address is valid.';
      } else if (error.code === 'INSUFFICIENT_FUNDS') {
        errorMessage = 'Insufficient funds for gas. Please check your wallet balance.';
      } else if (error.code === 'USER_REJECTED') {
        errorMessage = 'Transaction rejected by user.';
      } else if (!window.ethereum) {
        errorMessage = 'MetaMask not detected. Please install MetaMask.';
      }

      setStatus({ 
        type: 'error', 
        message: `Error: ${errorMessage} ❌`
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="save-data-container">
      <div className="save-data-card">
        <div className="card-header">
          <h1>Save User Data</h1>
          {!isConnected ? (
            <button onClick={connectWallet} className="connect-wallet-btn">
              <i className="fas fa-wallet"></i> Connect Wallet
            </button>
          ) : (
            <div className="wallet-info">
              <i className="fas fa-check-circle"></i>
              Connected: {account.slice(0, 6)}...{account.slice(-4)}
            </div>
          )}
        </div>

        <form onSubmit={saveDataToBlockchain} className="save-data-form">
          <div className="form-group">
            <label>
              <i className="fas fa-user"></i> Name
            </label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter name"
              required
            />
          </div>

          <div className="form-group">
            <label>
              <i className="fas fa-address-card"></i> User Address
            </label>
            <input
              type="text"
              value={userAddress}
              onChange={(e) => setUserAddress(e.target.value)}
              placeholder="Enter user address"
              required
            />
          </div>

          <div className="form-group">
            <label>
              <i className="fas fa-map-marker-alt"></i> Location
            </label>
            <input
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="Enter location"
              required
            />
          </div>

          <button 
            type="submit" 
            className="submit-btn" 
            disabled={!isConnected || loading}
          >
            {loading ? (
              <>
                <div className="loader"></div>
                Saving Data...
              </>
            ) : (
              <>
                <i className="fas fa-save"></i> Save Data
              </>
            )}
          </button>
        </form>

        {status && (
          <div className={`status-message ${status.type}`}>
            {status.message}
          </div>
        )}
      </div>
    </div>
  );
};

export default SaveUserData;
