import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';

function SaveUserData() {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [status, setStatus] = useState({ type: '', message: '' });
  const [isConnected, setIsConnected] = useState(false);
  const [account, setAccount] = useState('');

  const contractAddress = '0x7635615a00cbC897Bd020468C4338B194C8CC948';

  // Check if wallet is connected on component mount
  useEffect(() => {
    checkIfWalletIsConnected();
    // Add listener for account changes
    if (window.ethereum) {
      window.ethereum.on('accountsChanged', handleAccountsChanged);
      window.ethereum.on('chainChanged', () => window.location.reload());
    }
    return () => {
      if (window.ethereum) {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
      }
    };
  }, []);

  const handleAccountsChanged = (accounts) => {
    if (accounts.length > 0) {
      setAccount(accounts[0]);
      setIsConnected(true);
    } else {
      setAccount('');
      setIsConnected(false);
    }
  };

  const checkIfWalletIsConnected = async () => {
    try {
      if (!window.ethereum) {
        setStatus({ type: 'error', message: 'Please install MetaMask!' });
        return;
      }

      const accounts = await window.ethereum.request({ method: 'eth_accounts' });
      if (accounts.length > 0) {
        setAccount(accounts[0]);
        setIsConnected(true);
      }
    } catch (error) {
      console.error('Error checking wallet connection:', error);
    }
  };

  const connectWallet = async () => {
    try {
      if (!window.ethereum) {
        setStatus({ type: 'error', message: 'Please install MetaMask!' });
        return;
      }

      setStatus({ type: 'info', message: 'Connecting to MetaMask...' });
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setAccount(accounts[0]);
      setIsConnected(true);
      setStatus({ type: 'success', message: 'Wallet connected successfully!' });
    } catch (error) {
      console.error('Error connecting wallet:', error);
      setStatus({ type: 'error', message: 'Error connecting wallet. Please try again.' });
    }
  };

  const validateInputWithAI = async (input, field) => {
    try {
      console.log('Sending to AI for validation:', { input, field });
      const response = await fetch('http://localhost:5002/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          data: input,
          name: field === 'name' ? input : '',
          address: field === 'address' ? input : '',
          location: field === 'location' ? input : ''
        })
      });

      if (!response.ok) {
        console.error('AI service response not OK:', response.status);
        return { 
          is_malicious: false,  // Allow input if AI service is not responding
          reason: 'AI validation service unavailable',
          confidence: 0
        };
      }

      const analysis = await response.json();
      console.log('AI Analysis result:', analysis);

      return {
        is_malicious: analysis.prediction === 'attack',
        reason: analysis.prediction === 'attack' ? 
          `Malicious content detected in ${analysis.field}` : 'Input is safe',
        confidence: 1.0
      };
    } catch (error) {
      console.error('Error in AI validation:', error);
      // Allow input if AI service is not available
      return { 
        is_malicious: false,
        reason: 'AI validation service unavailable',
        confidence: 0
      };
    }
  };

  const saveDataToBlockchain = async () => {
    if (!isConnected) {
      setStatus({ type: 'error', message: 'Please connect your wallet first!' });
      return;
    }

    try {
      setStatus({ type: 'info', message: 'Performing security validation...' });

      // Final security check with AI model
      const response = await fetch('http://localhost:5002/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          data: JSON.stringify({ name, userAddress, location }),
          name: name,
          address: userAddress,
          location: location
        })
      });

      if (!response.ok) {
        throw new Error('Security validation failed');
      }

      const securityCheck = await response.json();

      if (securityCheck.prediction === 'attack') {
        setStatus({
          type: 'error',
          message: `Security Alert: Malicious content detected. Data cannot be saved.`
        });
        return;
      }

      setStatus({ type: 'info', message: 'Security check passed. Saving to blockchain...' });

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const signerAddress = await signer.getAddress();

      console.log('Connected with address:', signerAddress);

      const contract = new ethers.Contract(
        contractAddress,
        UserDataStorageAbi,
        signer
      );

      console.log('Saving data:', { name, userAddress, location });

      // Create transaction
      const tx = await contract.saveUserData(
        name,
        userAddress,
        location,
        {
          from: signerAddress,
          gasLimit: ethers.parseUnits("300000", "wei")
        }
      );

      console.log('Transaction hash:', tx.hash);
      setStatus({ type: 'info', message: 'Transaction sent! Waiting for confirmation...' });

      const receipt = await tx.wait();
      console.log('Transaction confirmed:', receipt);

      if (receipt.status === 1) {
        setStatus({ type: 'success', message: 'Data saved successfully! ✅' });
        setName('');
        setUserAddress('');
        setLocation('');
      } else {
        setStatus({ type: 'error', message: 'Transaction failed. Please try again. ❌' });
      }

    } catch (error) {
      console.error('Save error:', error);
      if (error.message === 'Security validation failed') {
        setStatus({ type: 'error', message: 'Security validation failed. Please check your inputs. ❌' });
      } else if (error.code === 'ACTION_REJECTED') {
        setStatus({ type: 'error', message: 'Transaction was rejected by user. ❌' });
      } else if (error.code === 'INSUFFICIENT_FUNDS') {
        setStatus({ type: 'error', message: 'Error: Insufficient funds for gas. Please check your balance. ❌' });
      } else {
        setStatus({ type: 'error', message: 'Error saving data: ' + (error.message || 'Unknown error') + ' ❌' });
      }
    }
  };

  async function checkTransaction(txData) {
    try {
      const response = await fetch('/api/analyze-transaction', {
        method: 'POST',
        body: JSON.stringify(txData)
      });

      const analysis = await response.json();

      if (analysis.is_suspicious) {
        displayWarning({
          riskLevel: analysis.risk_level,
          score: analysis.risk_score,
          details: analysis.risk_factors
        });
      }

    } catch (error) {
      console.error('Error analyzing transaction:', error);
    }
  }

  const handleInputChange = async (e, field) => {
    const value = e.target.value;

    // First update UI immediately for responsiveness
    switch(field) {
      case 'name':
        setName(value);
        break;
      case 'address':
        setUserAddress(value);
        break;
      case 'location':
        setLocation(value);
        break;
    }

    try {
      // Then check with AI
      const analysis = await validateInputWithAI(value, field);

      if (analysis.is_malicious) {
        setStatus({
          type: 'error',
          message: `Security Alert: ${analysis.reason} (Confidence: ${Math.round(analysis.confidence * 100)}%)`
        });

        // Reset the field if malicious content detected
        switch(field) {
          case 'name':
            setName('');
            break;
          case 'address':
            setUserAddress('');
            break;
          case 'location':
            setLocation('');
            break;
        }
      } else if (analysis.confidence === 0) {
        // AI service is unavailable, show a warning but allow input
        console.warn('AI validation service unavailable');
        setStatus({
          type: 'info',
          message: 'Security validation service temporarily unavailable'
        });
        setTimeout(() => setStatus({ type: '', message: '' }), 3000); // Clear message after 3 seconds
      } else {
        // Clear any error messages if input is safe
        setStatus({ type: '', message: '' });
      }
    } catch (error) {
      console.error('Error in input handling:', error);
      // Don't block input on error, just show a warning
      setStatus({
        type: 'info',
        message: 'Input validation temporarily unavailable'
      });
      setTimeout(() => setStatus({ type: '', message: '' }), 3000); // Clear message after 3 seconds
    }
  };

  return (
    <div style={styles.formContainer}>
      <h2 style={styles.title}>Save User Data</h2>

      {!isConnected ? (
        <div style={styles.connectContainer}>
          <p style={styles.description}>Please connect your wallet to save data.</p>
          <button onClick={connectWallet} style={styles.connectButton}>
            Connect Wallet
          </button>
        </div>
      ) : (
        <>
          <div style={styles.connectedStatus}>
            <span style={styles.connectedIcon}></span>
            Connected: {account.slice(0, 6)}...{account.slice(-4)}
          </div>

          <p style={styles.description}>Enter your details to save them to the blockchain.</p>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => handleInputChange(e, 'name')}
              placeholder="Enter your name"
              style={styles.input}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Address</label>
            <input
              type="text"
              value={userAddress}
              onChange={(e) => handleInputChange(e, 'address')}
              placeholder="Enter your Ethereum address"
              style={styles.input}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Location</label>
            <input
              type="text"
              value={location}
              onChange={(e) => handleInputChange(e, 'location')}
              placeholder="Enter your location"
              style={styles.input}
            />
          </div>

          <button onClick={saveDataToBlockchain} style={styles.button}>
            Save Data
          </button>
        </>
      )}

      {status.message && (
        <div style={{
          ...styles.status,
          backgroundColor: status.type === 'error' ? '#FEE2E2' : 
                         status.type === 'success' ? '#DCFCE7' :
                         status.type === 'info' ? '#E0E7FF' : '#F8FAFC',
          color: status.type === 'error' ? '#DC2626' :
                status.type === 'success' ? '#059669' :
                status.type === 'info' ? '#4F46E5' : '#64748B',
        }}>
          {status.message}
        </div>
      )}
    </div>
  );
}

