import React, { useState } from 'react';
import { ethers } from 'ethers';
import UserDataStorageAbi from './abis/UserDataStorage.json'; // Ensure this ABI file exists

function App() {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [status, setStatus] = useState('');

  // Replace with your deployed contract address
  const contractAddress = '0xYourContractAddressHere';

  const saveDataToBlockchain = async () => {
    if (!window.ethereum) {
      alert('Please install MetaMask!');
      return;
    }

    try {
      // Request account access
      await window.ethereum.request({ method: 'eth_requestAccounts' });

      // Create a provider and signer
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();

      // Create a contract instance
      const contract = new ethers.Contract(contractAddress, UserDataStorageAbi.abi, signer);

      // Call saveUserData function in the contract
      const tx = await contract.saveUserData(name, userAddress, location);
      setStatus('Saving data...');

      // Wait for the transaction to be mined
      await tx.wait();
      setStatus('Data saved successfully!');
    } catch (error) {
      console.error(error);
      setStatus('Error saving data.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif', maxWidth: '600px', margin: 'auto' }}>
      <h1 style={{ color: '#333' }}>Store User Data</h1>
      <p style={{ marginBottom: '20px' }}>
        Enter your details below to save your name, address, and location on the blockchain.
      </p>

      <div style={{ marginBottom: '20px' }}>
        <label>
          <strong>Name:</strong>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter your name"
            style={{
              marginLeft: '10px',
              padding: '8px',
              width: '100%',
              maxWidth: '300px',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />
        </label>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <label>
          <strong>Address:</strong>
          <input
            type="text"
            value={userAddress}
            onChange={(e) => setUserAddress(e.target.value)}
            placeholder="Enter your address"
            style={{
              marginLeft: '10px',
              padding: '8px',
              width: '100%',
              maxWidth: '300px',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />
        </label>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <label>
          <strong>Location:</strong>
          <input
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            placeholder="Enter your location"
            style={{
              marginLeft: '10px',
              padding: '8px',
              width: '100%',
              maxWidth: '300px',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          />
        </label>
      </div>

      <button
        onClick={saveDataToBlockchain}
        style={{
          padding: '10px 20px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
          fontSize: '16px',
        }}
      >
        Save Data
      </button>

      {status && (
        <p style={{ marginTop: '20px', color: status.includes('Error') ? 'red' : 'green' }}>
          {status}
        </p>
      )}
    </div>
  );
}

export default App;
