import React, { useState, useEffect } from 'react';
import { BrowserProvider, Contract } from 'ethers';
import UserDataStorageAbi from '../abis/UserDataStorage.json';
import './SaveUserData.css';

const SaveUserData = () => {
  const [name, setName] = useState('');
  const [userAddress, setUserAddress] = useState('');
  const [location, setLocation] = useState('');
  const [loading, setLoading] = useState(false);
  const [status, setStatus] = useState({ type: '', message: '' });
  const [isConnected, setIsConnected] = useState(false);
  const [account, setAccount] = useState('');
  const [aiAnalysisComplete, setAiAnalysisComplete] = useState(false);
  const contractAddress = '0x7635615a00cbC897Bd020468C4338B194C8CC948';

  useEffect(() => {
    checkIfWalletIsConnected();
    window.ethereum.on('accountsChanged', handleAccountsChanged);
    window.ethereum.on('chainChanged', () => window.location.reload());

    return () => {
      window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
    };
  }, []);

  const checkIfWalletIsConnected = async () => {
    try {
      const accounts = await window.ethereum.request({ method: 'eth_accounts' });
      if (accounts.length > 0) {
        setAccount(accounts[0]);
        setIsConnected(true);
      }
    } catch (error) {
      console.error('Error checking wallet connection:', error);
    }
  };

  const handleAccountsChanged = (accounts) => {
    if (accounts.length > 0) {
      setAccount(accounts[0]);
      setIsConnected(true);
    } else {
      setIsConnected(false);
      setAccount('');
    }
  };

  const connectWallet = async () => {
    try {
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setAccount(accounts[0]);
      setIsConnected(true);
    } catch (error) {
      console.error('Error connecting wallet:', error);
    }
  };

  const saveDataToBlockchain = async (e) => {
    e.preventDefault();
    setLoading(true);
    setAiAnalysisComplete(false);
    setStatus({ type: 'info', message: 'Analyzing data with AI...' });

    try {
      // First, analyze data with AI
      const aiResponse = await fetch('http://localhost:5002/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, userAddress, location })
      });

      if (!aiResponse.ok) {
        const errorData = await aiResponse.json().catch(() => ({}));
        throw new Error(errorData.error || 'AI analysis failed');
      }

      const aiResult = await aiResponse.json();
      setAiAnalysisComplete(true);
      setStatus({ type: 'success', message: 'AI analysis complete ✅ Saving to blockchain...' });

      // Then save to blockchain
      const provider = new BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new Contract(contractAddress, UserDataStorageAbi, signer);

      const tx = await contract.saveUserData(name, userAddress, location, {
        gasLimit: 300000
      });

      setStatus({ type: 'info', message: 'Transaction sent! Waiting for confirmation...' });

      const receipt = await tx.wait(1);

      if (receipt.status === 1) {
        setStatus({ type: 'success', message: 'Data saved successfully! ✅' });
        setName('');
        setUserAddress('');
        setLocation('');
        setAiAnalysisComplete(false);
      } else {
        setStatus({ type: 'error', message: 'Transaction failed. Please try again. ❌' });
      }
    } catch (error) {
      console.error('Error:', error);
      setStatus({ 
        type: 'error', 
        message: `Error: ${error.message.includes('AI analysis') ? 
          'AI analysis failed. Please try again. ❌' : 
          error.message} ❌`
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="save-data-container">
      <div className="save-data-card">
        <div className="card-header">
          <div className="header-title">
            <i className="fas fa-save"></i>
            <h1>Save User Data</h1>
          </div>
          {!isConnected ? (
            <button onClick={connectWallet} className="connect-wallet-btn">
              <i className="fas fa-wallet"></i> Connect Wallet
            </button>
          ) : (
            <div className="wallet-info">
              <i className="fas fa-check-circle"></i>
              <span>Connected:</span>
              <span className="address" title={account}>
                {account.slice(0, 6)}...{account.slice(-4)}
              </span>
            </div>
          )}
        </div>

        <form onSubmit={saveDataToBlockchain} className="save-data-form">
          <div className="form-group">
            <label>
              <i className="fas fa-user"></i> Name
            </label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter name"
              required
              className={aiAnalysisComplete ? 'validated' : ''}
            />
          </div>

          <div className="form-group">
            <label>
              <i className="fas fa-address-card"></i> User Address
            </label>
            <input
              type="text"
              value={userAddress}
              onChange={(e) => setUserAddress(e.target.value)}
              placeholder="Enter user address"
              required
              className={aiAnalysisComplete ? 'validated' : ''}
            />
          </div>

          <div className="form-group">
            <label>
              <i className="fas fa-map-marker-alt"></i> Location
            </label>
            <input
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="Enter location"
              required
              className={aiAnalysisComplete ? 'validated' : ''}
            />
          </div>

          <button 
            type="submit" 
            className={`submit-btn ${loading ? 'loading' : ''}`}
            disabled={!isConnected || loading}
          >
            {loading ? (
              <>
                <i className="fas fa-spinner fa-spin"></i>
                {status.message.includes('AI analysis') ? 'Analyzing...' : 'Saving Data...'}
              </>
            ) : (
              <>
                <i className="fas fa-save"></i> Save Data
              </>
            )}
          </button>
        </form>

        {status.message && (
          <div className={`status-message ${status.type}`}>
            <i className={`fas ${
              status.type === 'success' ? 'fa-check-circle' :
              status.type === 'error' ? 'fa-exclamation-circle' :
              'fa-info-circle'
            }`}></i>
            {status.message}
          </div>
        )}
      </div>
    </div>
  );
};

export default SaveUserData;
